import React, { Component, Fragment } from 'react';
//引入组件
import { Select, List } from 'antd';
//引入图标
import { CloseCircleOutlined, PlusCircleOutlined } from '@ant-design/icons';
//引入css文件
import './style.css';
//从组件中获取组件
const { Option } = Select;


class Content_third extends Component {
    constructor(props) {
        super(props);
        this.state = {
            list: []
        }
    }
    // 添加
    iconAddClick = () => {
        const list = [...this.state.list, 1]
        this.setState({
            list
        })
    }
    //删除 
    handleItemClick = (index) => {
        let list = [...this.state.list]
        list[index] = 0
        this.setState({
            list
        })
    }
    //循环渲染每一项
    getListItems = () => {
        return this.state.list.map((item, index) => {
            if (this.state.list[index] === 1)
                return (
                    <List.Item className='listitem'>
                        <Select
                            key={item + 'h'}
                            className='select_middle'
                            defaultValue='事件名'
                        >
                            {this.mapRenderEventOption()};
                    </Select>
                        <Select
                            key={item + "x"}
                            className='select_middle'
                            defaultValue="关联属性名"
                        >
                            {this.mapRenderAttrOption()};
                    </Select>
                        <CloseCircleOutlined
                            style={{ display: 'inline-block', marginRight: 10 }}
                            key={item + 'y'}
                            className='icon'
                            onClick={this.handleItemClick.bind(this, index)}
                        />
                    </List.Item>
                )
        })
    }
    //添加事件的数据
    mapRenderEventOption = () => {
        return this.props.events.map(item => {
            return (
                <Option key={item.id} value={item.event} >{item.name}</Option>
            )
        })
    }
    //添加属性的数据
    mapRenderAttrOption = () => {
        return this.props.attrs.map(item => {
            return (
                <Option key={item.id + 's'}>{item.name}</Option>
            )
        })
    }

    render() {
        return (
            <Fragment>
                <PlusCircleOutlined
                    className='icon'
                    style={{ position: 'relative', top: -21, left: 84 }}
                    onClick={this.iconAddClick}
                />
                <div className='eventComponent eventComponent2' >
                    <List
                        itemLayout="horizontal"
                    // dataSource={this.state.list}
                    // renderItem={(item) => (
                    //     this.getListItems(item)
                    // )}
                    >
                        {this.getListItems()}
                    </List>
                </div>
            </Fragment>
        )
    }

}

export default Content_third;
