import React, { Component } from 'react';
import ReactDom from 'react-dom';
import axios from 'axios';
//引入组件
import { Layout } from 'antd';
import Content_first from './components/Content/目标转化事件/index';
import Content_second from './components/Content/待归因事件/index';
import Content_third from './components/Content/过程事件/index';
import Content_fourth from './components/Content/查看方式/index';
import DataUplode from './components/Content/DataUplode/index';
import ReasultShow from './components/Footer/ResultShow/index';
//引入图标
import { SendOutlined } from '@ant-design/icons';
//引入css文件
import 'antd/dist/antd.css';
import './style.css';

const { Header, Footer, Content } = Layout;

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            events: [],
            attrs: [],
            values: [],
            sendData: {
                targetEvents: "",
                processEvents: [
                    ""
                ],
                touchEvents: [
                    ""
                ],
                valueAttr: "",
                beginTime: '',
                endTime: '',
                mode: '',
                window: '',
                otherConverse: '',
                N: ''
            }
        }

    }

    componentDidMount() {
        //接口请求数据
        //events
        axios.get('/events')
            .then((res) => {
                this.setState({
                    events: res.data
                })
                // console.log(this.state.events)
            })
            .catch((err) => {
                console.log(err)
            })
        //attrs
        axios.get('/attrs')
            .then((res) => {
                this.setState({
                    attrs: res.data
                })
                // console.log(this.state.attrs)
            })
            .catch((err) => {
                console.log(err)
            })
        //values
        axios.get('/values')
            .then((res) => {
                this.setState({
                    values: res.data
                })
                // console.log(this.state.values)
            })
            .catch((err) => {
                console.log(err)
            })
        //发送请求
        // axios.post('/result', this.state.sendData)
        //     .then(res => {
        //         console.log(res.data)
        //     })
        //     .catch(err => {
        //         console.log(err)
        //     })
    }

    //获取目标转化事件的数据
    acqTargetEventData = (value) => {
        this.setState({
            targetEvents:value
        })
    }
    //获取待归因事件的数据
    acqProcessEventData = (value) => {
        this.setState({
            targetEvents:value
        })
        this.state.sendData.processEvents = value
    }
    //获取过程事件的数据
    acqTouchEventData = (value) => {
        this.setState({
            targetEvents:value
        })
        this.state.sendData.touchEvents= value
    }
    //获取查看方式的数据
    acqValueAttrData = (value) => {
        this.setState({
            targetEvents:value
        })
        this.state.sendData.valueAttr = value
    }
    //获取时间的数据
    acqStartTimeData = (value) => {
        this.setState({
            targetEvents:value
        })
        this.state.sendData.beginTime= value
    }
    acqEndTimeData = (value) => {
        this.setState({
            targetEvents:value
        })
        this.state.sendData.endTime= value
    }
    //获取归因模型的数据
    acqGuiyinmodeData = (value) => {
        this.setState({
            targetEvents:value
        })
        this.state.sendData.mode = value
    }
    //获取归因窗口期的数据
    acqGuiyinWindowData = (value) => {
        this.setState({
            targetEvents:value
        })
        this.state.sendData.window = value
    }
    //获取是否转于其他规划的数据
    acqOtherConverseData = (value) => {
        this.setState({
            targetEvents:value
        })
        this.state.sendData.otherConverse = value
    }
    //获取展示条数的数据
    acqNData = (value) => {
        this.setState({
            N:value
        })
        this.state.sendData.N = value
        console.log(this.state.sendData)
    }

    render() {
        return (
            <Layout className='layout'>
                <Header className='header'>
                    归因分析
                </Header>
                <form action='/result' method='post'>
                    <Content className='content'>
                        <h1 className='primarytitle'><SendOutlined className='icon icon_top' />选择事件</h1>
                        <div id='top'>
                            <div className='contentfirst'>
                                <span className='secondarytitle span'>目标转化事件：</span>
                                <Content_first events={this.state.events} attrs={this.state.attrs} acqTargetEventData={this.acqTargetEventData} />
                            </div>
                            <div className='contentsecond'>
                                <p className='secondarytitle'>待归因事件：</p>
                                <Content_second events={this.state.events} attrs={this.state.attrs} acqProcessEventData={this.acqProcessEventData} />
                            </div>
                            <div className='contentthird'>
                                <p className='secondarytitle'>过程事件：</p>
                                <Content_third events={this.state.events} attrs={this.state.attrs} acqTouchEventData={this.acqTouchEventData} />
                            </div>
                            <div className='contentfourth'>
                                <p className='secondarytitle'>查看方式：</p>
                                <Content_fourth values={this.state.values} acqValueAttrData={this.acqValueAttrData} />
                            </div>
                        </div>
                        <div className='middle' >
                            <div className='contentfifth'>
                                <DataUplode
                                    acqStartTimeData={this.acqStartTimeData}
                                    acqEndTimeData={this.acqEndTimeData}
                                    acqGuiyinmodeData={this.acqGuiyinmodeData}
                                    acqGuiyinWindowData={this.acqGuiyinWindowData}
                                    acqOtherConverseData={this.acqOtherConverseData}
                                    acqNData={this.acqNData}
                                />
                            </div>
                        </div>
                    </Content>
                </form>
                <Footer className='footer'>
                    <ReasultShow />
                </Footer>
            </Layout>
        )
    }
}

ReactDom.render(<App />, document.getElementById('root'))
