import React, { Component, Fragment } from 'react';
//引入组件
import { Select, List } from 'antd';
//引入图标
import { CloseCircleOutlined, PlusCircleOutlined } from '@ant-design/icons';
//引入css文件
import './style.css';
//从组件中获取组件
const { Option } = Select;

let tag = 0;

class Content_second extends Component {
    constructor(props) {
        super(props);
        this.state = {
            list: []
        }
    }
    // 添加
    iconAddClick = () => {
        tag++;
        const list = [...this.state.list, tag]
        this.setState({
            list
        })
    }
    //删除 
    handleItemClick = (index) => {
        let list = [...this.state.list]
        list.splice(index, 1)
        console.log(index)
        this.setState({
            list
        })
    }
    //第一个下拉选择框保存value值
    firstSelectChange = (e) => {
        let list = [...this.state.list]
        list.push(e)
        this.setState({
            list
        })
    }
    //第二个下拉选择框保存value值
    secondSelectChange = (e) => {
        console.log(e.target)
    }
    //循环渲染每一项
    getListItems = () => {
        return this.state.list.map((item, index) => {
            return (
                <List.Item className='listitem'>
                    <Select
                        // key={item + 'h'}
                        className='select'
                        defaultValue='事件名'
                        onChange={this.firstSelectChange}
                    >
                        {this.mapRenderEventOption()};
                    </Select>
                    <Select
                        // key={item + "x"}
                        className='select'
                        defaultValue="分组属性名"
                        onChange={this.secondSelectChange}
                    >
                        {this.mapRenderAttrOption()};
                    </Select>
                    <CloseCircleOutlined
                        key={item + 'y'}
                        className='icon'
                        onClick={this.handleItemClick.bind(this, index)}
                    />
                </List.Item>
            )
        })
    }
    //添加事件的数据
    mapRenderEventOption = () => {
        return this.props.events.map(item => {
            return (
                <Option key={item.id + 'c'}>{item.name}</Option>
            )
        })
    }
    //添加属性的数据
    mapRenderAttrOption = () => {
        return this.props.attrs.map(item => {
            return (
                <Option key={item.id + 's'}>{item.name}</Option>
            )
        })
    }

    render() {
        return (
            <Fragment>
                <PlusCircleOutlined
                    className='icon'
                    style={{ position: 'relative', top: -218, left: 100 }}
                    onClick={this.iconAddClick}
                />
                <div className='eventComponent eventComponent2' >
                    <List
                        itemLayout="horizontal"
                    // dataSource={this.state.list}
                    // renderItem={(item) => (
                    //     this.getListItems(item)
                    // )}
                    >
                        {this.getListItems()}
                    </List>
                </div>
            </Fragment>
        )
    }

}

export default Content_second;
