import React, { Component } from 'react';
import { Select } from 'antd';

const { Option } = Select;
const event = ['Zhejiang', 'Jiangsu'];
const attribute = {
    Zhejiang: ['Hangzhou', 'Ningbo', 'Wenzhou'],
    Jiangsu: ['Nanjing', 'Suzhou', 'Zhenjiang'],
};

const Content_first = () => {
    const [cities, setCities] = React.useState(attribute[event[0]]);
    const [secondCity, setSecondCity] = React.useState(attribute[event[0]][0]);

    const handleEventChange = value => {
        setCities(attribute[value]);
        setSecondCity(attribute[value][0]);
    };

    const onSecondCityChange = value => {
        setSecondCity(value);
    };

    return (
        <>
            <p>选择事件</p>
            <Select defaultValue={event[0]} style={{ width: 120 }} onChange={handleEventChange}>
                {event.map(province => (
                    <Option key={province}>{province}</Option>
                ))}
            </Select>
            <Select style={{ width: 120 }} value={secondCity} onChange={onSecondCityChange}>
                {cities.map(city => (
                    <Option key={city}>{city}</Option>
                ))}
            </Select>
        </>
    );
};

export default Content_first;
