import React, { Component } from 'react';
//引入组件
import { Select, ist, Avatar } from 'antd';
//引入图标
import { CloseCircleOutlined } from '@ant-design/icons';
//引入css文件
import './style.css';
//从组件中获取组件
const { Option } = Select;

class Content_second extends Component {

    mapRenderEventOption = () => {
        return this.props.events.map(item => {
            return (
                <Option key={item.id}>{item.name}</Option>
            )
        })
    }

    mapRenderAttrOption = () => {
        return this.props.attrs.map(item => {
            return (
                <Option key={item.id}>{item.name}</Option>
            )
        })
    }

    render() {
        return (
            <div className='eventComponent'>
                <List
                    itemLayout="horizontal"
                    dataSource={data}
                    renderItem={item => (
                        <List.Item>
                            <List.Item.Meta
                                avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                                title={<a href="https://ant.design">{item.title}</a>}
                                description="Ant Design, a design language for background applications, is refined by Ant UED Team"
                            />
                        </List.Item>
                    )}
                />
                <Select
                    className='select'
                    defaultValue='事件名'
                    style={{ width: 120 }}
                >
                    {this.mapRenderEventOption()};
                </Select>
                <Select
                    className='select'
                    defaultValue="分组属性名"
                    style={{ width: 120 }}
                >
                    {this.mapRenderAttrOption()};
                </Select>
                <CloseCircleOutlined />
            </div>
        )
    }

}

export default Content_second;
