import React, { Component, Fragment } from 'react';
//引入组件
import { Chart } from '@antv/g2';
import { Table } from 'antd';
//引入css文件
import './style.css';

const { Column, ColumnGroup } = Table;
const data = [
  { year: '1951 年', sales: 38 },
  { year: '1952 年', sales: 52 },
  { year: '1956 年', sales: 61 },
  { year: '1957 年', sales: 145 },
  { year: '1958 年', sales: 48 },
  { year: '1959 年', sales: 38 },
  { year: '1960 年', sales: 38 },
  { year: '1962 年', sales: 38 },
];
const chart = new Chart({
  container: 'container',
  autoFit: true,
  height: 500,
});

chart.data(data);
chart.scale('sales', {
  nice: true,
});

chart.tooltip({
  showMarkers: false
});
chart.interaction('active-region');

chart.interval().position('year*sales');

chart.render();

class ReasultShow extends Component {

    constructor(props) {
        super(props);
        this.state = {
            data: [
                // {
                //     key: '1',
                //     touchEvents: 'John',
                //     totalClicks: 'Brown',
                //     clickRate: 32,
                //     durationDistr: '',
                //     stepLengthDistr: ['nice', 'developer'],
                //     valueRealization: 'valueRealization',
                //     contributionRate: 'contributionRate'
                // }
            ]
        }
    }

    //返回结果数据渲染到页面
    componentWillReceiveProps = () => {
        let update = function () {
            let data = [];
            const result = this.props.resultData
            if ( result[0] != '' && result[0] !== undefined && result != '' ) {
                for (let i = 0; i < this.props.sendData.N; i++) {
                    data.push({
                        key: i,
                        touchEvents: result[0][i],
                        totalClicks: result[1][i],
                        clickRate: `${result[2][i]} (${result[3][i] * 100 + '%'})`,
                        durationDistr: result[4][i],
                        stepLengthDistr: result[5][i],
                        // durationDistr: result[4][i],
                        // stepLengthDistr: result[5][i],
                        valueRealization: result[6][i],
                        contributionRate: result[7][i]
                    }
                    )
                }
            }
            return data
        }
        update = update.bind(this)
        let data = update();
        this.setState({
            data
        })
    }

    showColumn = () => {
        return (
            <>
                <ColumnGroup title="待归因事件">
                    <Column title="触点事件" dataIndex="touchEvents" key="touchEvents" />
                </ColumnGroup>
                <ColumnGroup title="点击规模">
                    <Column title="总点击数" dataIndex="totalClicks" key="totalClicks" />
                    <Column title="有效点击率" dataIndex="clickRate" key="clickRate" />
                </ColumnGroup>
                <ColumnGroup title="转化分布">
                    <Column title="转化时长分布" dataIndex="durationDistr" key="durationDistr" />
                    <Column title="转化步长分布" dataIndex="stepLengthDistr" key="stepLengthDistr" />
                </ColumnGroup>
                <ColumnGroup title="目标转化">
                    <Column title="实现价值" dataIndex="valueRealization" key="valueRealization" />
                    <Column title="贡献率" dataIndex="contributionRate" key="contributionRate" />
                </ColumnGroup>
            </>
        )
    }


    render() {
        return (
            <Fragment>
                <Table
                    bordered={true}
                    dataSource={this.state.data}
                    className='reasult'
                    pagination={{
                        position: ['bottomCenter'],
                        pageSize: 10,
                        // 只有一页是不显示分页按钮
                        hideOnSinglePage: true,
                    }}
                    scroll={{
                        y: 290
                    }}
                    tableLayout='fixed'
                    style={{ minWidth: 700 }}
                >
                    {this.showColumn()}
                </Table>
            </Fragment>
        )
    }
}

export default ReasultShow;
