import React from 'react';
//引入组件
import { Select } from 'antd';
const { Option } = Select;
//定义变量储存数据
const eventData = ['Zhejiang', 'Jiangsu'];
const attributeData = {
    Zhejiang: ['Hangzhou', 'Ningbo', 'Wenzhou'],
    Jiangsu: ['Nanjing', 'Suzhou', 'Zhenjiang'],
};

const Content_first = () => {
    const [events, setevents] = React.useState(attributeData[eventData[0]]);
    const [attributes, setAttributes] = React.useState(attributeData[eventData[0]][0]);

    const handleEventChange = value => {
        setevents(attributeData[value]);
        setAttributes(attributeData[value][0]);
    };

    const onAttributeChange = value => {
        setAttributes(value);
    };

    return (
        <>
            <p>选择事件</p>
            <Select
             defaultValue={eventData[0]}
              style={{ width: 120 }}
               onChange={handleEventChange}>
                {eventData.map(item => (
                    <Option key={item}>{item}</Option>
                ))}
            </Select>
            <Select
             style={{ width: 120 }} value={attributes} onChange={onAttributeChange}>
                {events.map(item => (
                    <Option key={item}>{item}</Option>
                ))}
            </Select>
        </>
    );
};

export default Content_first;
