import React, { Component, Fragment } from 'react';
//引入组件
import { Select, List } from 'antd';
//引入图标
import { CloseCircleOutlined, PlusCircleOutlined } from '@ant-design/icons';
//引入css文件
import './style.css';
//从组件中获取组件
const { Option } = Select;

let tag = -1;

class Content_second extends Component {
    constructor(props) {
        super(props);
        this.state = {
            list: []
        }
    }

    iconAddClick = () => {
        tag++;
        const list = [...this.state.list,tag]
        console.log(list)
        this.setState({
            list
        })
    }

    iconRemoveClick = (index) => {
        const list = this.state.list
        const newlist = list.splice(index,1)
        // this.setState({
        //     list: newlist
        // })
    }

    showListItem = (index) => {
        return (
            <List.Item>
                <Select
                    className='select'
                    defaultValue='事件名'
                    style={{ width: 120 }}
                >
                    {this.mapRenderEventOption()};
                </Select>
                <Select
                    className='select'
                    defaultValue="分组属性名"
                    style={{ width: 120 }}
                >
                    {this.mapRenderAttrOption()};
                </Select>
                <CloseCircleOutlined className='icon' onClick={this.iconRemoveClick(index)} />
            </List.Item>
        )
    }

    mapRenderEventOption = () => {
        return this.props.events.map(item => {
            return (
                <Option key={item.id}>{item.name}</Option>
            )
        })
    }

    mapRenderAttrOption = () => {
        return this.props.attrs.map(item => {
            return (
                <Option key={item.id}>{item.name}</Option>
            )
        })
    }

    render() {
        return (
            <Fragment>
                <PlusCircleOutlined
                    className='icon'
                    style={{ position: 'relative', top: -240, left: 100 }}
                    onClick={this.iconAddClick}
                />
                <div className='eventComponent eventComponent2' >
                    <List
                        itemLayout="horizontal"
                        dataSource={this.state.list}
                        renderItem={(item) => (
                            this.showListItem(item)
                        )}
                    />
                </div>
            </Fragment>

        )
    }

}

export default Content_second;
