import React from 'react';
//引入组件
import { Select } from 'antd';
const { Option } = Select;
//定义变量储存数据
const eventData = ['Zhejiang', 'Jiangsu'];
const attribute = {
    Zhejiang: ['Hangzhou', 'Ningbo', 'Wenzhou'],
    Jiangsu: ['Nanjing', 'Suzhou', 'Zhenjiang'],
};

const Content_first = () => {
    const [events, setevents] = React.useState(attribute[eventData[0]]);
    const [secondCity, setAttributes] = React.useState(attribute[eventData[0]][0]);

    const handleEventChange = value => {
        setevents(attribute[value]);
        setAttributes(attribute[value][0]);
    };

    const onAttributeChange = value => {
        setAttributes(value);
    };

    return (
        <>
            <p>选择事件</p>
            <Select defaultValue={event[0]} style={{ width: 120 }} onChange={handleEventChange}>
                {event.map(province => (
                    <Option key={province}>{province}</Option>
                ))}
            </Select>
            <Select style={{ width: 120 }} value={secondCity} onChange={onAttributeChange}>
                {eventData.map(city => (
                    <Option key={city}>{city}</Option>
                ))}
            </Select>
        </>
    );
};

export default Content_first;
