import React, { Component, Fragment } from 'react';
//引入组件
import { Select, List } from 'antd';
//引入图标
import { CloseCircleOutlined, PlusCircleOutlined } from '@ant-design/icons';
//引入css文件
import './style.css';
//从组件中获取组件
const { Option } = Select;

class Content_second extends Component {
    constructor(props) {
        super(props);
        this.state = {
            list: [1, 1]
        }
    }

    iconClick = () => {
        let list = this.state.list.push(1)
        console.log(this.state.list)
        this.setState({
            list
        })
    }

    showListItem = () => {
        return (
            <List.Item>
                <Select
                    className='select'
                    defaultValue='事件名'
                    style={{ width: 120 }}
                >
                    {this.mapRenderEventOption()};
            </Select>
                <Select
                    className='select'
                    defaultValue="分组属性名"
                    style={{ width: 120 }}
                >
                    {this.mapRenderAttrOption()};
            </Select>
                <CloseCircleOutlined className='icon' />
            </List.Item>
        )
    }

    mapRenderEventOption = () => {
        return this.props.events.map(item => {
            return (
                <Option key={item.id}>{item.name}</Option>
            )
        })
    }

    mapRenderAttrOption = () => {
        return this.props.attrs.map(item => {
            return (
                <Option key={item.id}>{item.name}</Option>
            )
        })
    }

    render() {
        return (
            <Fragment>
                <PlusCircleOutlined
                    className='icon'
                    style={{ position: 'relative', top: -240, left: 100 }}
                    onClick={this.iconClick}
                />
                <div className='eventComponent eventComponent2' >
                    <List

                        itemLayout="horizontal"
                        dataSource={this.state.list}
                        renderItem={() => (
                            this.showListItem()
                        )}
                    />
                </div>
            </Fragment>

        )
    }

}

export default Content_second;
