import React from 'react';
import axios from 'axios';
//引入组件
import { Select } from 'antd';
//引入css文件
import './style.css';
//从组件中引入组件
const { Option } = Select;
//定义变量储存数据
const eventData = ['Zhejiang', 'Jiangsu'];
const attrData = {
    Zhejiang: ['Hangzhou', 'Ningbo', 'Wenzhou'],
    Jiangsu: ['Nanjing', 'Suzhou', 'Zhenjiang'],
};

const Content_first = () => {
    axios.get('http://localhost:3000/events')
    .then((res) => {
        console.log(res.data)
    })
    .catch((err) => {
        console.log(err)
    })

    const [events, setevents] = React.useState(attrData[eventData[0]]);
    const [attrs, setattrs] = React.useState(attrData[eventData[0]][0]);

    const handleEventChange = value => {
        setevents(attributeData[value]);
        setattrs(attributeData[value][0]);
    };

    const onAttributeChange = value => {
        setattrs(value);
    };


    return (
        <div className='eventComponent'>
            <span className='secondarytitle'>目标转化事件：</span>
            <Select
                className="select"
                defaultValue={eventData[0]}
                style={{ width: 120 }}
                onChange={handleEventChange}>
                {eventData.map(item => (
                    <Option key={item}>{item}</Option>
                ))}
            </Select>
            <Select
                style={{ width: 120 }}
                value={attrs}
                onChange={onAttributeChange}>
                {events.map(item => (
                    <Option key={item}>{item}</Option>
                ))}
            </Select>
        </div>
    );
};

export default Content_first;
